import React, { Component } from 'react'

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: [],
      beers: []
    } 
  }

componentDidMount = () =>{
  fetch('https://api.punkapi.com/v2/beers')

  .then(response => response.json())
  .then ( data =>{
    let beers = data.map(beer => beer);
    this.setState = {
      beers
    }

  })
}  




 render() {
   return(
    <div>
    <header className="App-header">
    <div>
      {this.state.beers}
    </div>
    </header> 
    </div>
   )
 }
}






export default App;
